import React, { useState, useEffect } from 'react';

const form = () => {

    const [formData, setFormData] = useState({
        name: "",
        description: "",
        diet: "",
        weight: "",
        length: "",
        found_in: "",
        image: "https://static.tvtropes.org/pmwiki/pub/images/land_before_time10_disneyscreencapscom_5285.jpg",
        sound: "/roars/Dino.mp3"
    })

    const onChange = (e) => {
        const newFormData = Object.assign({}, formData);
        newFormData[e.target.name] = e.target.value;
        setFormData(newFormData);
    }

    const onSubmit = (e) => {
        e.preventDefault();
        const tempFormData = formData;

        // postDinosaur(tempFormData).then((data) => {
        //   addDinosaur(data);
        // })

        console.log(dinosaurs);
        // Reset the form input values   
        setFormData({
            name: "",
            description: "",
            diet: "",
            weight: "",
            length: "",
            found_in: "",
            image: "An Image"
        });
    }

    return (
        <form onSubmit={onSubmit} id="flashcard-form" >
            <h2 className="formheader">Add a Dinosaur</h2>
            <div className="formWrap">
                <label htmlFor="name">Name:</label>
                <input
                    onChange={onChange}
                    type="text"
                    id="name"
                    name="name"
                    value={formData.name} />
            </div>
            <div className="formWrap">
                <label htmlFor="description">Description:</label>
                <input
                    onChange={onChange}
                    type="text"
                    id="description"
                    name="description"
                    value={formData.description} />
            </div>
            <div className="formWrap">
                <label htmlFor="diet">Diet:</label>
                <input
                    onChange={onChange}
                    type="diet"
                    id="diet"
                    name="diet"
                    value={formData.diet} />
            </div>
            <div className="formWrap">
                <label htmlFor="weight">Weight:</label>
                <input
                    onChange={onChange}
                    type="weight"
                    id="weight"
                    name="weight"
                    value={formData.weight} />
            </div>
            <div className="formWrap">
                <label htmlFor="length">Length:</label>
                <input
                    onChange={onChange}
                    type="length"
                    id="length"
                    name="length"
                    value={formData.length} />
            </div>
            <div className="formWrap">
                <label htmlFor="found_in">Found In:</label>
                <input
                    onChange={onChange}
                    type="found_in"
                    id="found_in"
                    name="found_in"
                    value={formData.found_in} />
            </div>

            <input type="submit" value="Save" id="save" className="formheader" />
        </form>
    )
}

export default form
