import React, { useState, useEffect } from 'react';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';

const Form = () => {

    const [position, setPosition] = React.useState([51.505, -0.09]); // Set initial position to London

    const [formData, setFormData] = useState({
        name: "",
        category: "",
        description: "",
        location: "",
        email: "",
        address: "",
    })

    const onChange = (e) => {
        const newFormData = Object.assign({}, formData);
        newFormData[e.target.name] = e.target.value;
        setFormData(newFormData);
    }

    const onSubmit = (e) => {
        e.preventDefault();
        const tempFormData = formData;
        console.log(tempFormData);
        // postDinosaur(tempFormData).then((data) => {
        //   addDinosaur(data);
        // })

        // Reset the form input values   
        setFormData({
            name: "",
            category: "",
            description: "",
            location: "",
            email: "",
            address: ""
        });
    }

    return (
        <form onSubmit={onSubmit} id="flashcard-form" >
            <h2 className="formheader">Jim'll Fix It</h2>

            <div className="formWrap">
                <select name="category" id="category" value={formData.category} onChange={onChange}>
                    <option value="">--Please choose a category--</option>
                    <option value="bins">Bins</option>
                    <option value="grass">Grass</option>
                    <option value="roads">Roads</option>
                    <option value="lights">Street lights</option>
                    <option value="schools">Schools</option>
                    <option value="other">Other</option>
                </select>
            </div>

            <div className="formWrap">
                <label htmlFor="name">Your name:</label>
                <input
                    onChange={onChange}
                    type="text"
                    id="name"
                    name="name"
                    value={formData.name} />
            </div>

            <div className="formWrap">
                <label htmlFor="description">Description:</label>
                <input
                    onChange={onChange}
                    type="text"
                    id="description"
                    name="description"
                    value={formData.description} />
            </div>
            <div className="formWrap">
                <label htmlFor="location">Location:</label>
                <input
                    onChange={onChange}
                    type="text"
                    id="location"
                    name="location"
                    value={formData.location} />
            </div>
            <div className="formWrap">
                <label htmlFor="email">Email:</label>
                <input
                    onChange={onChange}
                    type="email"
                    id="email"
                    name="email"
                    value={formData.email} />
            </div>
            <div className="formWrap">
                <label htmlFor="address">Address:</label>
                <input
                    onChange={onChange}
                    type="text"
                    id="address"
                    name="address"
                    value={formData.address} />
            </div>

            <input type="submit" value="Tell Cllr Jim" id="save" className="formheader" />
        </form>
    )
}

export default Form
