import React, { useState, useEffect } from 'react';

const Form = () => {

    const [formData, setFormData] = useState({
        name: "",
        category: "",
        description: "",
        location: "",
        email: "",
        address: "",
    })

    const onChange = (e) => {
        const newFormData = Object.assign({}, formData);
        newFormData[e.target.name] = e.target.value;
        setFormData(newFormData);
    }

    const onSubmit = (e) => {
        e.preventDefault();
        const tempFormData = formData;
        console.log(tempFormData);
        // postDinosaur(tempFormData).then((data) => {
        //   addDinosaur(data);
        // })

        // Reset the form input values   
        setFormData({
            name: "",
            category: "",
            description: "",
            location: "",
            email: "",
            address: ""
        });
    }

    return (
        <form onSubmit={onSubmit} id="flashcard-form" >
            <h2 className="formheader">What's the matter?</h2>
            <div className="formWrap">
                <label htmlFor="name">Your name:</label>
                <input
                    onChange={onChange}
                    type="text"
                    id="name"
                    name="name"
                    value={formData.name} />
            </div>
            <div className="formWrap">
                <label htmlFor="category">Choose issue:</label>
                <input
                    onChange={onChange}
                    type="text"
                    id="category"
                    name="category"
                    value={formData.category} />
            </div>
            <div className="formWrap">
                <label htmlFor="description">Description:</label>
                <input
                    onChange={onChange}
                    type="text"
                    id="description"
                    name="description"
                    value={formData.description} />
            </div>
            <div className="formWrap">
                <label htmlFor="location">Location:</label>
                <input
                    onChange={onChange}
                    type="text"
                    id="location"
                    name="location"
                    value={formData.location} />
            </div>
            <div className="formWrap">
                <label htmlFor="email">Email:</label>
                <input
                    onChange={onChange}
                    type="text"
                    id="email"
                    name="email"
                    value={formData.email} />
            </div>
            <div className="formWrap">
                <label htmlFor="address">Address:</label>
                <input
                    onChange={onChange}
                    type="text"
                    id="address"
                    name="address"
                    value={formData.address} />
            </div>

            <input type="submit" value="Save" id="save" className="formheader" />
        </form>
    )
}

export default Form
